package service;

import model.Book;

import java.util.ArrayList;
import java.util.List;

public class BookService implements iService<Book>{
    private List<Book> bookList = new ArrayList<>();
    public BookService(){
        bookList.add(new Book(1, "God Father", 180000,"Tiểu thuyết tội phạm","Mario Puzo","data:image/jpeg;base64,"));
        bookList.add(new Book(2, "Nếu còn có ngày mai", 110000,"Tiểu thuyết trinh thám","Sidney Sheldon","https://cdn0.fahasa.com/media/catalog/product/8/9/8935095622580_1.jpg"));
        bookList.add(new Book(3, "Bá tước Monte Cristo", 140000,"Tiểu thuyết phiêu lưu","Alexandre Dumas","https://salt.tikicdn.com/cache/w1200/ts/product/69/44/b7/4786e775168434f38594ccca92194872.jpg"));
    }
    @Override
    public boolean add(Book book) {
        bookList.add(book);
        return true;
    }

    @Override
    public boolean edit(Book book, int id) {
        int index = findById(id);
        if (index != -1) {
            bookList.set(index,book);
            return true;
        }else {
            return false;
        }
    }

    @Override
    public boolean delete(int id) {
        int index = findById(id);
        if (index != -1) {
            bookList.remove(index);
            return true;
        }else {
            return false;
        }
    }

    @Override
    public int findById(int id) {
        for (int i = 0; i < bookList.size() ; i++) {
            if (bookList.get(i).getId()==id) return i;
        }
        return -1;
    }

    @Override
    public List<Book> findAll() {
        return bookList;
    }
}
